    raise HTTPException(status_code=404, detail="placeholder.svg not found")

@app.get("/{full_path:path}", response_class=HTMLResponse)
def serve_spa(request: Request, full_path: str):
    index_path = os.path.join(SPA_DIST, "index.html")
    if os.path.exists(index_path):
        return FileResponse(index_path)
    raise HTTPException(status_code=404, detail="index.html not found. Make sure the frontend is built.")

