from fastapi.staticfiles import StaticFiles
import httpx

sys.path.insert(0, os.path.abspath(os.path.join(os.path.dirname(__file__), '..')))

app = FastAPI(
    docs_url=None,
    redoc_url=None,
    openapi_url=None
)

FRONT_DIR = os.path.dirname(__file__)
SPA_DIST = os.path.join(FRONT_DIR, 'web', 'dist', 'spa')
ASSETS_DIST = os.path.join(SPA_DIST, 'assets')

if os.path.exists(ASSETS_DIST):
    app.mount("/assets", StaticFiles(directory=ASSETS_DIST), name="assets")

BACKEND_URL = "http://back:8000"

@app.api_route("/api/{path:path}", methods=["GET", "POST", "DELETE"])
async def proxy_api(request: Request, path: str):
    async with httpx.AsyncClient(base_url=BACKEND_URL) as client:
        url = httpx.URL(path=f"/api/{path}", query=request.url.query.encode("utf-8"))
        headers = dict(request.headers)
        headers['host'] = 'back'

        body = await request.body()

        try:
            backend_response = await client.request(
                request.method, str(url), content=body, headers=headers, timeout=60.0
            )

            excluded_headers = ['content-encoding', 'content-length', 'transfer-encoding', 'connection']
            response_headers = {
                key: value for key, value in backend_response.headers.items()
                if key.lower() not in excluded_headers
            }

            return Response(
                content=backend_response.content,
                status_code=backend_response.status_code,
                headers=response_headers,
            )
        except httpx.ConnectError as e:
            raise HTTPException(status_code=503, detail=f"Service unavailable: cannot connect to backend. {e}")
        except httpx.TimeoutException as e:
            raise HTTPException(status_code=504, detail=f"Gateway timeout: backend took too long to respond. {e}")

